import{_ as e,c as l,o as t,ag as o}from"./chunks/framework.BDwTZuFy.js";const u=JSON.parse('{"title":"CDN","description":"","frontmatter":{},"headers":[],"relativePath":"docs/前端工程化/CDN加速.md","filePath":"docs/前端工程化/CDN加速.md","lastUpdated":1756707094000}'),i={name:"docs/前端工程化/CDN加速.md"};function n(r,a,d,c,s,h){return t(),l("div",null,a[0]||(a[0]=[o('<h1 id="cdn" tabindex="-1">CDN <a class="header-anchor" href="#cdn" aria-label="Permalink to &quot;CDN&quot;">​</a></h1><blockquote><p>CDN 的全称是 Content Delivery Network，即内容分发网络。 是一种用于加速网络内容传输的技术和架构。它通过将内容分布到全球各地的服务器节点，使得用户可以从离他们更近的服务器上获取所需的内容，从而减少了网络延迟和提高了内容的传输速度。</p></blockquote><h2 id="cdn-优点" tabindex="-1">CDN 优点 <a class="header-anchor" href="#cdn-优点" aria-label="Permalink to &quot;CDN 优点&quot;">​</a></h2><ol><li>显著提升用户访问速度</li><li>CDN 起到了分流作用，减轻了源站的负载。</li></ol><h2 id="cdn-如何查找最优的节点" tabindex="-1">CDN 如何查找最优的节点 <a class="header-anchor" href="#cdn-如何查找最优的节点" aria-label="Permalink to &quot;CDN 如何查找最优的节点&quot;">​</a></h2><ol><li>根据用户的 IP 地址，查看与用户最近的节点</li><li>考虑各个节点的负载情况，依据最小连接数、最快响应时间等指标来选择最优的节点</li></ol><h2 id="项目中使用-cdn-的最佳实践" tabindex="-1">项目中使用 CDN 的最佳实践 <a class="header-anchor" href="#项目中使用-cdn-的最佳实践" aria-label="Permalink to &quot;项目中使用 CDN 的最佳实践&quot;">​</a></h2><ol><li>只外链“体积大、稳定、命名全局清晰”的依赖，临时性或者变化快的包继续本地打包</li><li>避免混用同一依赖的 “本地ESM 和 CDN 全局” 两套实例(尤其是 Vue/React)，确保全局单例</li></ol><h3 id="多外链的书写顺序" tabindex="-1">多外链的书写顺序 <a class="header-anchor" href="#多外链的书写顺序" aria-label="Permalink to &quot;多外链的书写顺序&quot;">​</a></h3><ol><li>框架全局（Vue、React）</li><li>依赖该框架的库（Element-UI、Ant-Design-Vue、Vue Router、Pinia）</li><li>工具库（axios、lodash、moment）</li><li>应用入口（index.js）</li></ol>',10)]))}const D=e(i,[["render",n]]);export{u as __pageData,D as default};
