import{_ as l,c as i,o as e,ag as t}from"./chunks/framework.BDwTZuFy.js";const r="/assets/00001.CC9U-4yV.png",o="/assets/0002.DKJWV1_B.png",s="/assets/0003.Byyd31ro.png",_=JSON.parse('{"title":"计算机网络的基础知识点","description":"","frontmatter":{},"headers":[],"relativePath":"docs/计算机网络/计算机网络/计算机网络微课堂/基础知识点.md","filePath":"docs/计算机网络/计算机网络/计算机网络微课堂/基础知识点.md","lastUpdated":1712646874000}'),h={name:"docs/计算机网络/计算机网络/计算机网络微课堂/基础知识点.md"};function n(d,a,p,c,u,b){return e(),i("div",null,a[0]||(a[0]=[t('<h1 id="计算机网络的基础知识点" tabindex="-1">计算机网络的基础知识点 <a class="header-anchor" href="#计算机网络的基础知识点" aria-label="Permalink to &quot;计算机网络的基础知识点&quot;">​</a></h1><h2 id="三种交换方式" tabindex="-1">三种交换方式 <a class="header-anchor" href="#三种交换方式" aria-label="Permalink to &quot;三种交换方式&quot;">​</a></h2><p><img src="'+r+'" alt="图片" title="0001"></p><h2 id="计算机网络的性能指标" tabindex="-1">计算机网络的性能指标 <a class="header-anchor" href="#计算机网络的性能指标" aria-label="Permalink to &quot;计算机网络的性能指标&quot;">​</a></h2><h3 id="速率" tabindex="-1">速率 <a class="header-anchor" href="#速率" aria-label="Permalink to &quot;速率&quot;">​</a></h3><ul><li>一个比特(bit)就是一个0或者1 8bit = 1Byte(B) KB = 2的十次方B</li><li>bit/s(b/s,bps) kb/s = 10的三次方 b/s</li><li>速率中的b是1比特 大小中的B是8比特</li></ul><h3 id="带宽" tabindex="-1">带宽 <a class="header-anchor" href="#带宽" aria-label="Permalink to &quot;带宽&quot;">​</a></h3><ul><li>表示通信线路所能传递数据的最高数据率</li><li>带宽就像马路的宽度，速率就是真实在路上跑的汽车和时间的比值</li></ul><h3 id="吞吐量" tabindex="-1">吞吐量 <a class="header-anchor" href="#吞吐量" aria-label="Permalink to &quot;吞吐量&quot;">​</a></h3><ul><li>单位时间内通过某个网络的数据量</li></ul><h3 id="时延" tabindex="-1">时延 <a class="header-anchor" href="#时延" aria-label="Permalink to &quot;时延&quot;">​</a></h3><ul><li>发送时延 传播时延 处理时延</li><li>发送时延中的发送速率取决于网卡的发送速率，信道带宽，接口速率中的最小值(木桶效应)</li></ul><h3 id="时延带宽积" tabindex="-1">时延带宽积 <a class="header-anchor" href="#时延带宽积" aria-label="Permalink to &quot;时延带宽积&quot;">​</a></h3><ul><li>等于传播时延乘以带宽(类似于求圆柱体的体积)</li></ul><h3 id="往返时间" tabindex="-1">往返时间 <a class="header-anchor" href="#往返时间" aria-label="Permalink to &quot;往返时间&quot;">​</a></h3><h3 id="利用率" tabindex="-1">利用率 <a class="header-anchor" href="#利用率" aria-label="Permalink to &quot;利用率&quot;">​</a></h3><ul><li>利用率在50%时比较好，既不浪费通道资源，也不会因为利用率太高而导致时延过大</li></ul><h3 id="丢包率" tabindex="-1">丢包率 <a class="header-anchor" href="#丢包率" aria-label="Permalink to &quot;丢包率&quot;">​</a></h3><ul><li>网络拥塞过高会导致过高的丢包率</li></ul><h2 id="计算机网络体系结构" tabindex="-1">计算机网络体系结构 <a class="header-anchor" href="#计算机网络体系结构" aria-label="Permalink to &quot;计算机网络体系结构&quot;">​</a></h2><p><img src="'+o+'" alt="计算机网络体系结构" title="计算机网络体系结构"><img src="'+s+'" alt="计算机网络体系结构中的术语" title="计算机网络体系结构中的术语"></p><h2 id="mac地址-ip地址-arp地址" tabindex="-1">MAC地址 IP地址 ARP地址 <a class="header-anchor" href="#mac地址-ip地址-arp地址" aria-label="Permalink to &quot;MAC地址 IP地址 ARP地址&quot;">​</a></h2><ul><li><p>MAC地址(物理地址)</p></li><li><p>MAC 地址是以太网的 MAC 子层所使用的地址，属于数据链路层</p></li><li><p>每个网络适配器都有一个全球唯一的 MAC 地址。MAC 是对网络上各接口(设备有多个网卡的话就会有多个 MAC 地址)的唯一标识，而不是对网络上各设备的唯一标识。</p></li><li><p>IP 地址</p></li><li><p>属于 TCP/IP 协议结构网际层所使用的的地址</p></li><li><p>IP 地址分为网络编号和主机编号</p></li><li><p>每个网络都有唯一的编号(路由器的作用就是连接不同的网络)，一起连接在同一个网络上的不同主机或者路由器的 IP 地址网络编号前缀是相同的，后面的主机编号是不同的。</p></li><li><p>ARP协议</p></li><li><p>属于 TCP/IP 协议结构网际层，作用是通过 IP 地址找到 MAC 地址。</p></li><li><p>ARP 协议发送请求报文(使用广播地址FF-FF-FF-FF)来获取对应 ip 地址的 MAC 地址。</p></li><li><p>ARP 协议只能在一段链路或者一个网络上进行使用，不能跨网络使用</p></li></ul><p>MAC 地址必须是在同一个网络中，IP 地址是网络的网络。 在数据传输过程中，源 IP 地址始终不变，MAC 地址必须是相邻的地址</p>',24)]))}const m=l(h,[["render",n]]);export{_ as __pageData,m as default};
