import{_ as e,c as l,o as t,ag as i}from"./chunks/framework.BDwTZuFy.js";const r="/assets/0004.DkMg0dWc.jpg",u=JSON.parse('{"title":"物理层","description":"","frontmatter":{},"headers":[],"relativePath":"docs/计算机网络/计算机网络/计算机网络微课堂/物理层.md","filePath":"docs/计算机网络/计算机网络/计算机网络微课堂/物理层.md","lastUpdated":1712646874000}'),o={name:"docs/计算机网络/计算机网络/计算机网络微课堂/物理层.md"};function s(p,a,c,d,n,_){return t(),l("div",null,a[0]||(a[0]=[i('<h1 id="物理层" tabindex="-1">物理层 <a class="header-anchor" href="#物理层" aria-label="Permalink to &quot;物理层&quot;">​</a></h1><ul><li>为了解决在各种传输媒体上传输比特0和1的问题。</li><li>物理层为数据链路层屏蔽了各种传输媒体的差异，使数据链路层只需要考虑如何完成本层的协议和服务，而不必考虑具体的网络传输媒体是什么。</li><li>各种传输媒体并不在计算机网络的任何体系结构中，属于比物理层还下面的概念。</li></ul><h2 id="传输方式" tabindex="-1">传输方式 <a class="header-anchor" href="#传输方式" aria-label="Permalink to &quot;传输方式&quot;">​</a></h2><ul><li><p>远距离的传输(计算机网络)使用的是串行传输</p></li><li><p>计算机内部(cpu和内存之间通过总线传输)使用的是并行传输，也就是我们常说的32位机和64位机</p></li><li><p>同步传输</p></li><li><p>异步传输</p></li><li><p>单向通信(单工) 无线电 广播等</p></li><li><p>双向交替通信(半双工) 对讲机</p></li><li><p>双向同时通信(全双工) 电话</p></li></ul><h2 id="编码和调制" tabindex="-1">编码和调制 <a class="header-anchor" href="#编码和调制" aria-label="Permalink to &quot;编码和调制&quot;">​</a></h2><p><img src="'+r+'" alt="编码" title="编码方式"></p>',6)]))}const m=e(o,[["render",s]]);export{u as __pageData,m as default};
