import{_ as l,c as e,o as i,ag as t}from"./chunks/framework.BDwTZuFy.js";const P=JSON.parse('{"title":"计算机网络中每层的协议以及协议的主要作用","description":"","frontmatter":{},"headers":[],"relativePath":"docs/计算机网络/计算机网络/计算机网络微课堂/网络中每层的协议.md","filePath":"docs/计算机网络/计算机网络/计算机网络微课堂/网络中每层的协议.md","lastUpdated":1712646874000}'),r={name:"docs/计算机网络/计算机网络/计算机网络微课堂/网络中每层的协议.md"};function o(h,a,d,n,u,s){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="计算机网络中每层的协议以及协议的主要作用" tabindex="-1">计算机网络中每层的协议以及协议的主要作用 <a class="header-anchor" href="#计算机网络中每层的协议以及协议的主要作用" aria-label="Permalink to &quot;计算机网络中每层的协议以及协议的主要作用&quot;">​</a></h1><h2 id="应用层-报文" tabindex="-1">应用层(报文) <a class="header-anchor" href="#应用层-报文" aria-label="Permalink to &quot;应用层(报文)&quot;">​</a></h2><ul><li>直接为用户的的应用进程提供服务(电子邮件，文件传输等)</li></ul><h3 id="http协议" tabindex="-1">HTTP协议 <a class="header-anchor" href="#http协议" aria-label="Permalink to &quot;HTTP协议&quot;">​</a></h3><ul><li>无连接，无状态，可靠连接(HTTP可靠是因为TCP是可靠的)</li></ul><h3 id="ftp协议" tabindex="-1">FTP协议 <a class="header-anchor" href="#ftp协议" aria-label="Permalink to &quot;FTP协议&quot;">​</a></h3><h3 id="dns协议" tabindex="-1">DNS协议 <a class="header-anchor" href="#dns协议" aria-label="Permalink to &quot;DNS协议&quot;">​</a></h3><h2 id="运输层-报文段-用户数据报" tabindex="-1">运输层(报文段/用户数据报) <a class="header-anchor" href="#运输层-报文段-用户数据报" aria-label="Permalink to &quot;运输层(报文段/用户数据报)&quot;">​</a></h2><ul><li>定义了一些传输数据的协议和端口号</li><li>负责向两个主机中的进程之间的通信提供服务</li></ul><h3 id="tcp协议-报文段" tabindex="-1">TCP协议(报文段) <a class="header-anchor" href="#tcp协议-报文段" aria-label="Permalink to &quot;TCP协议(报文段)&quot;">​</a></h3><ul><li>面向连接的，报文段，可靠传输。只支持单播</li></ul><h3 id="udp协议-用户数据报" tabindex="-1">UDP协议(用户数据报) <a class="header-anchor" href="#udp协议-用户数据报" aria-label="Permalink to &quot;UDP协议(用户数据报)&quot;">​</a></h3><ul><li>无连接的，用户数据报，不可靠传输，支持单播多播广播</li></ul><h2 id="网络层-ip数据报" tabindex="-1">网络层(IP数据报) <a class="header-anchor" href="#网络层-ip数据报" aria-label="Permalink to &quot;网络层(IP数据报)&quot;">​</a></h2><ul><li>在位于不同地理位置的网络中的两个主机系统之间提供连接和路径选择。</li><li>负责网上不同主机之间提供通信服务</li><li>选择合适的路由，使得数据能通过网络中的路由器找到目的主机。</li></ul><h3 id="ip协议" tabindex="-1">IP协议 <a class="header-anchor" href="#ip协议" aria-label="Permalink to &quot;IP协议&quot;">​</a></h3><ul><li>提供面向无连接，无状态，不可靠传输服务。</li></ul><h2 id="数据链路层-帧" tabindex="-1">数据链路层(帧) <a class="header-anchor" href="#数据链路层-帧" aria-label="Permalink to &quot;数据链路层(帧)&quot;">​</a></h2><ul><li>定义了如何让格式化数据以进行传输，以及如何对控制对物理介质的访问。这一层通常还提供错误检测和纠正，以确保数据的可靠传输。、</li><li>链路 就是从一个结点到相邻结点的一段物理线路，中间没有任何交换结点(没有路由器)</li></ul><h2 id="物理层-比特" tabindex="-1">物理层(比特) <a class="header-anchor" href="#物理层-比特" aria-label="Permalink to &quot;物理层(比特)&quot;">​</a></h2><ul><li>主要定义物理设备标准，如网线的接口类型、光纤的接口类型、各种传输介质的传输速率等。它的主要作用是传输比特流（就是由1、0转化为电流强弱来进行传输,到达目的地后在转化为1、0，也就是我们常说的数模转换与模数转换）。这一层的数据叫做比特。</li><li>为了解决在各种传输媒体上传输比特0和1的问题。</li></ul>',21)]))}const _=l(r,[["render",o]]);export{P as __pageData,_ as default};
